CREATE TABLE IF NOT EXISTS `test_table`
(
  field       INTEGER PRIMARY KEY AUTOINCREMENT,
  secondField INTEGER      NOT NULL DEFAULT 0,
  thirdField  VARCHAR(255) NOT NULL DEFAULT 0,
  fourthField VARCHAR(255) NOT NULL DEFAULT 0,
  fifthField  INTEGER      NOT NULL DEFAULT 0,
  orderField  INTEGER      NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `test_table_joins`
(
  id               INTEGER PRIMARY KEY AUTOINCREMENT,
  test_table_field INTEGER      NOT NULL DEFAULT 0,
  joinedField      VARCHAR(255) NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `update_table`
(
  id      INTEGER PRIMARY KEY AUTOINCREMENT,
  column1 INTEGER      NOT NULL DEFAULT 0,
  column2 VARCHAR(255) NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `insert_table`
(
  id      INTEGER PRIMARY KEY AUTOINCREMENT,
  column1 INTEGER      NOT NULL DEFAULT 0,
  column2 VARCHAR(255) NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `delete_table`
(
  id      INTEGER PRIMARY KEY AUTOINCREMENT,
  column1 INTEGER      NOT NULL DEFAULT 0,
  column2 VARCHAR(255) NOT NULL DEFAULT 0
);
CREATE TABLE IF NOT EXISTS `users`
(
  id      INTEGER PRIMARY KEY AUTOINCREMENT,
  username VARCHAR(255) NOT NULL DEFAULT '',
  password VARCHAR(32) NOT NULL DEFAULT ''
);

